import React from 'react'
import './Contact.css'

const Contact =() =>{

    const form = useRef();

    const sendEmail = (e) => {
      e.preventDefault();

      emailjs.sendForm('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', form.current, 'YOUR_PUBLIC_KEY')
        .then((result) => {
            console.log(result.text);
        }, (error) => {
            console.log(error.text);
        });
    };


  return (
    <div className="contact">

        <div className="c-left">
             <div className="awesome">
                <span> Get in touch </span>
                 <span> Contact Me </span> 

            </div>
        </div>

        {/* right */}

        <div className="c-right">
            <form>
                <input type="text" name="user-name" className="user" placeholder="Name" />
                <input type="email" name="user-email" className="user" placeholder="Email" />
                <textarea input type="message"  className="user" placeholder="Message" />
                <input type="submit" value="Send" className="button s-button" />

            </form>
        </div>
    </div>
  )
}

export default Contact
